import React from 'react';
import ReactDOM from 'react-dom';
import './index.css'

let curDate = new Date(2021,7,5,14);
curDate = curDate.getHours();
let greeting = '';

const styling = { };

if(curDate >= 4 && curDate < 12)
{
  greeting = 'Hello Dear, Good Morning. We wish you a great day ahead.'
  styling.color = '#248f24'
}
else if(curDate >= 12 && curDate < 19) //19 means 7 p.m.
{
  greeting = 'Hello Dear, Good Afternoon. We hope you are having a fantastic day.'
  styling.color = '#ffa31a'
}
else
{
  greeting = 'Hello Dear, Good Night. Sweet Dreams.'
  styling.color = '#0000e6'
}

ReactDOM.render(
  <div>
  <h1><span style={styling}>{greeting}</span></h1>
  </div>,
  document.getElementById('root')
);
